function makeBoard(size){
	//assume size%2==0
	
	items = [];
	for(var i=0;i<(size*size)/2;i++){
		/*push 2 of the same elements onto the items array*/
		items.push(i);
		items.push(i);
	}
	
	board = [];
	for(var i=0;i<size;i++){
		board[i]=[]
		for(var j=0;j<size;j++){
			/*get a random element from the items array*/
			var r = (Math.floor(Math.random()*items.length));
			/*splice returns the deleted elems in an array*/
			board[i][j]= items.splice(r,1)[0];  //remove item r from the array
		}
	}
	return board;
}

module.exports.makeBoard = makeBoard;